//typeCheck.js
var pattern_num = /[0-9]/;  // 숫자 
var pattern_eng = /[a-zA-Z]/;   // 문자 
var pattern_spc = /[~!@#$%^&*()_+|<>?:{}]/; // 특수문자
var pattern_kor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣]/; // 한글체크
 
function isNum(str){
    if(pattern_kor.test(str) || pattern_eng.test(str) || pattern_spc.test(str) || !pattern_num.test(str))
        return false;
    return true;
}
 
function isKor(str){
    if(pattern_num.test(str) || pattern_eng.test(str) || pattern_spc.test(str) || !pattern_kor.test(str))
        return false;
    return true;
}
 
function isEng(str){
    if(pattern_num.test(str) || pattern_kor.test(str) || pattern_spc.test(str) || !pattern_eng.test(str))
        return false;
    return true;
}
 
function isSpc(str){
    if(pattern_num.test(str) || pattern_kor.test(str) || pattern_eng.test(str) || !pattern_spc.test(str))
        return false;
    return true;
}
 
function isThereNum(str){
    if(!pattern_num.test(str))
        return false;
    return true;
}
 
function isThereKor(str){
    if(!pattern_kor.test(str))
        return false;
    return true;
}
 
function isThereEng(str){
    if(!pattern_eng.test(str))
        return false;
    return true;
}
 
function isThereSpc(str){
    if(!pattern_spc.test(str))
        return false;
    return true;
}